package Arithamaticoperation;

public class Arithamatic {
	static int firstvalue=40;
	static int secondvalue=20;
	static int thirdvalue=5;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Addition of "+firstvalue+" & "+secondvalue+" = "+ (firstvalue+secondvalue));
		System.out.println("substruction of "+firstvalue+" & "+secondvalue+" = "+ (firstvalue-secondvalue));
		System.out.println("multiplication of "+firstvalue+" & "+secondvalue+" = "+ (firstvalue*secondvalue));
		System.out.println("division of "+firstvalue+" & "+secondvalue+" = "+ (firstvalue/secondvalue));
		System.out.println("percentage of "+firstvalue+" & "+secondvalue+" = "+ (firstvalue%secondvalue));

	}

}
